import React from "react";

import classes from "./SiteButton.module.css";
import "../fonts/fonts.css";

const SiteButton = (props) => {
  return (
    <button
      className={classes.siteBtn}
      style={{
        backgroundColor: `${props.btnColor}`,
        color: `${props.textColor}`,
        width: `${props.width}`,
        height: "40px",
        top: `${props.top}`,
        left: `${props.left}`,
        borderRadius: "10px",
        border: "none",
        position: "absolute",
        transform: `translateX(${props.transX})`,
        bottom: "2000px",
      }}
    >
      <a href={props.link} target="_blank">
        <p
          className={classes.siteBtnText}
          style={{ fontFamily: "GmarketSansBold", fontSize: "14px" }}
        >
          SITE VIEW
        </p>
      </a>
    </button>
  );
};

export default SiteButton;
